mapper.select:
select a.id,u1.name as owner,a.name,a.start_date,a.end_date,a.cost,a.description,a.create_time, u2.name as create_by,a.edit_time,u3.name as edit_by from tbl_activity as a join tbl_user u1 on a.owner=u1.id join tbl_user u2 on a.create_by=u2.id left join tbl_user u3 on a.edit_by=u3.id
and a.name like concat('%',#{name},'%')
and u1.name like concat('%',#{owner},'%')
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}
order by a.create_time desc limit #{pageNo},#{pageSize}

select count(a.id) from tbl_activity as a join tbl_user u1 on a.owner=u1.id join tbl_user u2 on a.create_by=u2.id left join tbl_user u3 on a.edit_by=u3.id
and a.name like concat('%',#{name},'%')
and u1.name like concat('%',#{owner},'%')
and a.start_date >= #{startDate}
and a.end_date <= #{endDate}

mapper.delete:
delete from tbl_activity
where id in

  #{id}
